mapper.sql:
select * from product

	
		
			
			
			
				where p_division = #{p_division}
			
		
	
	
		where p_name like '%'|| #{p_name} ||'%'
	
	
		where p_name like '%'|| #{p_name} ||'%' and
		p_division = #{p_division}
	

order by p_number desc

mapper.select:
