mapper.select:
select ci_number, ci_title, a_id, to_char(ci_date, 'YYYY-MM-DD') as ci_date
from
( select list.*, rownum as rnum
	from(



				order by ci_number


				order by ci_date




				ASC


				DESC


	) list
)



			rnum between #{start_row} and #{end_row}

select nvl(count(1), 0) from
( select list.*, rownum as rnum
	from(

		order by ci_number desc
	) list
)

select ci_number, ci_title, a_id, ci_content, ci_image,   
to_char(ci_date,'YYYY-MM-DD HH24:MI:SS') as ci_date
from careinfo where ci_number = #{ci_number}
